public function getBpostOrder($order_id){
    $bpost_query = $this->db->query("SELECT bpost_id FROM `" . DB_PREFIX . "order_bpost_mapping` WHERE order_id = '" . (int)$order_id . "'");
    if ($bpost_query->num_rows) {
        $bpost_id = $bpost_query->row['bpost_id'];
    } else {
        $bpost_id = 0;
    }
    return $bpost_id;
}

